import {Component} from 'react'
import axios from 'axios';

import logo from './logo.svg';
import './App.css';

class App extends Component {
    constructor(props) {
        super(props);

        this.fileTypes = [{
            type: "book",
            text: "Books"
        }, {
            type: "image",
            text: "Images"
        }];

        this.state = {
            selectedFile: null,
            selectedFileType: this.fileTypes[0].type
        }
    }

    // On file select (from the pop up)
    onFileChange = event => {
        // Update the state
        this.setState({ selectedFile: event.target.files[0] });

        //console.log("File: " + this.state.selectedFile);
    };

    // On file type select
    onFileTypeChange = event => {
        console.log("Type 1: " + event.target.value);
        // Update the state
        this.setState({ selectedFileType: event.target.value });

       // console.log("Type: " + this.state.selectedFileType);
    };

    // On file upload (click the upload button)
    onFileUpload = () => {
        // Create an object of formData
        const formData = new FormData();

        // Update the formData object
        formData.append(
            "file",
            this.state.selectedFile,
            this.state.selectedFile.name
        );

        formData.append(
            "file_type",
            this.state.selectedFileType
        );

        // Request made to the backend api
        // Send formData object
        axios.post("http://192.168.0.160:8000/foo", formData);
    };

    // File content to be displayed after
    // file upload is complete
    fileData = () => {
        console.log(this.state.selectedFile);
        if (this.state.selectedFile) {
            return (
                <div>
                    <p>File Name: {this.state.selectedFile.name}</p>
                </div>
            );
        } 
    };

    getSelectOptions = () => {
        return (
            this.fileTypes.map(item => (
                <option value={item.type}>{item.text}</option>
            ))
        );
    }

    render() {
        // build the upload button
        let upLoadButton;
        if(this.state.selectedFile) {   
            upLoadButton = <button className="Left m-l-20px" onClick={this.onFileUpload}>Upload!</button>;
        }

        return (
            <>
                <div className="App">
                    <header className="App-header">
                        <img src={logo} className="App-logo" alt="logo" />
                    </header>
                    <h3>File Upload using React!</h3>
                    <p className="Left">
                        <input type="file" onChange={this.onFileChange} />
                        {upLoadButton}
                    </p>
                    <p className="Left">File Type: 
                        <select className="Left m-l-10px" onChange={this.onFileTypeChange} value={this.state.selectedFileType}>
                            {this.getSelectOptions()}
                        </select>
                    </p>
                    {this.fileData()}
                </div>
            </>
        );
    }
}

export default App;
