import logo from './logo.svg';
import './App.css';

const { Component } = require('react');

const axios = require('axios');

 // keep a util handy to dump objects
 const util = require('util');
 //console.log(util.inspect(error, {depth: null}))

 // grab our env variables
 const dotenv = require('dotenv');

 /*
console.log('--------------------------------------');
console.log(util.inspect(process.env, {depth: null}))
console.log('--------------------------------------');
*/

class App extends Component {
    constructor(props) {
        super(props);

        // grab our environemtn vars
        dotenv.config();

        /*
        this.fileTypes = [{
            type: "book",
            text: "Books",
            help: "Books must be in PDF format. Max file size is 1MB."
        }, {
            type: "image",
            text: "Images",
            help: "Images must be in JPG, JPEG, PNG, or GIF format. Max file size is 1MB."
        }];

                    fileType: this.fileTypes[0].type,
            fileTypeHelp: this.fileTypes[0].help,
        */

        //this.fileTypes = this.getFileTypeInfo();


        this.state = {
            apiUrl: 'http://' + process.env.REACT_APP_UPLOAD_API_HOST + ':' + process.env.REACT_APP_UPLOAD_API_PORT,
            selectOptions: null,
            fileTypeInfo: null,
            selectedFile: null,
            fileType: null,
            fileTypeHelp: null,
        };
    }

    componentDidMount() {
        axios.get(this.state.apiUrl + '/getFileTypes').then(response => {
            //this.fileTypeInfo = response.data;
            const data = response.data;

            this.setState({ fileTypeInfo: data });
            this.setState({ fileType: data[0].type });
            this.setState({ fileTypeHelp: data[0].help });

            console.log(data);

            const selectOptions = [];

            for(let i = 0; i < data.length; i++) {
                console.log(i)
                selectOptions.push(<option value={i}>{data[i].text}</option>);
            } 
            this.setState({ selectOptions: selectOptions });
        });
    }

    // grab the file type info
    getFileTypeInfo = () => {
        axios.get(this.apiUrl + '/getFileTypes').then(response => {
            //this.fileTypeInfo = response.data;

            this.setState({ fileTypeInfo: response.data });
            this.setState({ fileType: this.state.fileTypeInfo[0].type });
            this.setState({ fileTypeHelp: this.state.fileTypeInfo[0].help });
        });
    }

    getSelectOptions = () => {
        const selectOptions = [];

        for(let i = 0; i < this.state.fileTypeInfo.length; i++) {
            console.log(i)
            selectOptions.push(<option value={i}>{this.state.fileTypeInfo[i].text}</option>);
        } 
        this.setState({ selectOptions: selectOptions });
    }

    // On file select (from the pop up)
    onFileChange = event => {
        // Update the state
        this.setState({ selectedFile: event.target.files[0] });
    };

    // On file type select
    onTypeChange = event => {
        // Update the state
        this.setState({ fileType: this.fileTypeInfo[event.target.value].type });
        this.setState({ fileTypeHelp: this.fileTypeInfo[event.target.value].help });
    };

    // On description change
    onDescChange = event => {
        // Update the state
        this.setState({ description: event.target.value });
    };

    // On file upload (click the upload button)
    onSubmit = () => {
        // Create an object of formData
        const formData = new FormData();

        // Update the formData object
        formData.append(
            "file",
            this.state.selectedFile,
            this.state.selectedFile.name
        );

        formData.append(
            "file_type",
            this.state.fileType
        );

        formData.append(
            "description",
            this.state.description
        );

        // Request made to the backend api
        // Send formData object
        axios.post(this.apiUrl + '/foo', formData);
        //axios.post(this.apiUrl + '/upload', formData);
    };

    fileData = () => {
        //console.log(this.state.selectedFile);
        if (this.state.selectedFile) {
            return (
                <p className='m-t-10px'>File Name: {this.state.selectedFile.name}</p>
            );
        } 
    };

    render() {
        return (
            <>
                <div className='App'>
                    <header className='App-header'>
                        <img src={logo} className='App-logo' alt='logo' />
                        <h3>File Upload using React!</h3>
                    </header>
                    <div id='UploadTable'>
                        <table className='table table-hover table-bordered'>
                            <tbody>
                                <tr>
                                    <td className='w-15-pct'>Description</td>
                                    <td><input type='text' id='description' className='form-control' onChange={this.onDescChange} required /></td>
                                </tr>
                                <tr>
                                    <td className='w-15-pct'>Type</td>
                                    <td>
                                        <select className='Left m-l-10px' onChange={this.onTypeChange} value={this.state.fileType} required>
                                            {this.state.selectOptions}
                                        </select>
                                    </td>
                                </tr>
                                <tr>
                                    <td className='w-15-pct'>Image</td>
                                    <td>
                                            <input type='file' className='form-control' onChange={this.onFileChange} required />
                                            <small id='file-help' className='form-text text-muted'>{this.state.fileTypeHelp}</small>
                                    </td>
                                </tr>
                            </tbody>
                        </table>
                        <button type='button' className='btn btn-primary' onClick={this.onSubmit}>
                            <span></span> Upload File
                        </button>
                        {this.fileData()}
                    </div>
                </div>
            </>
        );
    }
}

export default App;
