import {Component} from 'react'
import axios from 'axios';

import logo from './logo.svg';
import './App.css';

class App extends Component {
    constructor(props) {
        super(props);

        this.fileTypes = [{
            type: "book",
            text: "Books",
            help: "Books must be in PDF format. Maximum file size is 1MB."
        }, {
            type: "image",
            text: "Images",
            help: "Images must be in JPG, JPEG, PNG, or GIF format. Maximum file size is 1MB."
        }];

        this.state = {
            selectedFile: null,
            fileType: this.fileTypes[0].type,
            fileTypeHelp: this.fileTypes[0].help,
        };
    }

    // On file select (from the pop up)
    onFileChange = event => {
        // Update the state
        this.setState({ selectedFile: event.target.files[0] });
    };

    // On file type select
    onTypeChange = event => {
        // Update the state
        this.setState({ fileType: this.fileTypes[event.target.value].type });
        this.setState({ fileTypeHelp: this.fileTypes[event.target.value].help });
    };

    // On description change
    onDescChange = event => {
        // Update the state
        this.setState({ description: event.target.value });
    };

    // On file upload (click the upload button)
    onSubmit = () => {
        // Create an object of formData
        const formData = new FormData();

        // Update the formData object
        formData.append(
            "file",
            this.state.selectedFile,
            this.state.selectedFile.name
        );

        formData.append(
            "file_type",
            this.state.fileType
        );

        formData.append(
            "description",
            this.state.description
        );

        // Request made to the backend api
        // Send formData object
        axios.post("http://192.168.0.160:8000/foo", formData);
    };

    fileData = () => {
        //console.log(this.state.selectedFile);
        if (this.state.selectedFile) {
            return (
                <div>
                    <p>File Name: {this.state.selectedFile.name}</p>
                </div>
            );
        } 
    };

    getSelectOptions = () => {
        let i = 0;
        return (
            this.fileTypes.map(item => (
                <option value={i++}>{item.text}</option>
            ))
        );
    }

    render() {
        return (
            <>
                <div className='App'>
                    <header className='App-header'>
                        <img src={logo} className='App-logo' alt='logo' />
                        <h3>File Upload using React!</h3>
                    </header>
                    <div id='UploadTable'>
                        <table className='table table-hover table-bordered'>
                            <tbody>
                                <tr>
                                    <td className='w-15-pct'>Description</td>
                                    <td><input type='text' id='description' className='form-control' value={this.state.description} onChange={this.onDescChange} required /></td>
                                </tr>
                                <tr>
                                    <td className='w-15-pct'>Type</td>
                                    <td>
                                        <select className='Left m-l-10px' onChange={this.onTypeChange} value={this.state.fileType} required>
                                            {this.getSelectOptions()}
                                        </select>
                                    </td>
                                </tr>
                                <tr>
                                    <td className='w-15-pct'>Image</td>
                                    <td>
                                            <input type='file' className='form-control' onChange={this.onFileChange} required />
                                            <small id='file-help' className='form-text text-muted'>{this.state.fileTypeHelp}</small>
                                    </td>
                                </tr>
                            </tbody>
                        </table>
                        <button type='button' className='btn btn-primary btn-sm m-r-10px' onClick={this.onSubmit}>
                            <span></span> Upload File
                        </button>
                        {this.fileData()}
                    </div>
                </div>
            </>
        );
    }
}

export default App;
