import logo from './logo.svg';
import './App.css';

const { Component } = require('react');

const axios = require('axios');

 // keep a util handy to dump objects
 const util = require('util');
 //console.log(util.inspect(error, {depth: null}))

 // grab our env variables
 const dotenv = require('dotenv');

 /*
console.log('--------------------------------------');
console.log(util.inspect(process.env, {depth: null}))
console.log('--------------------------------------');
*/

class App extends Component {
    constructor(props) {
        super(props);

        // grab our environemtn vars
        dotenv.config();

        // set "the state" (great old school show, btw)
        this.state = {
            apiUrl: 'http://' + process.env.REACT_APP_UPLOAD_API_HOST + ':' + process.env.REACT_APP_UPLOAD_API_PORT,
            selectOptions: [],
            selectValue: 0,
            fileTypeInfo: [],
            selectedFile: '',
            fileTypeHelp: ''
        };

        /*
        this.onFileChange = this.onFileChange.bind(this);
        this.onTypeChange = this.onTypeChange.bind(this);
        this.onDescChange = this.onDescChange.bind(this);
        this.onSubmit = this.onSubmit.bind(this);
        */
    }

    componentDidMount() {
        // grab our file type info and build the select options
        axios.get(this.state.apiUrl + '/getFileTypes').then(response => {
            const data = response.data.data;

            this.setState({ fileTypeInfo: data });
            this.setState({ fileTypeHelp: data[this.state.selectValue].help });

            const selectOptions = [];

            for(let i = 0; i < data.length; i++) {
                //console.log(i)
                selectOptions.push(<option value={i}>{data[i].text}</option>);
            } 
            this.setState({ selectOptions: selectOptions });
        });

        // grab our file type info and build the select options
        axios.get(this.state.apiUrl + '/getFiles').then(response => {
            const data = response.data.data;

            for(let i = 0; i < data.length; i++) {
                console.log(data[i]);
                //selectOptions.push(<option value={i}>{data[i].text}</option>);
            } 
            //this.setState({ selectOptions: selectOptions });
        });
    }

    // On file select (from the pop up)
    onFileChange = event => {
        // Update the state
        this.setState({ selectedFile: event.target.files[0] });
    };

    // On file type select
    onTypeChange = event => {
        // Update the state
        this.setState({ fileTypeHelp: this.state.fileTypeInfo[event.target.value].help });
        this.setState({ selectValue: event.target.value });
    };

    // On description change
    onDescChange = event => {
        // Update the state
        this.setState({ description: event.target.value });
    };

    // On file upload (click the upload button)
    onSubmit = () => {
        // Create an object of formData
        const formData = new FormData();

        // Update the formData object
        formData.append(
            "file",
            this.state.selectedFile,
            this.state.selectedFile.name
        );

        formData.append(
            "fileType",
            this.state.fileTypeInfo[this.state.selectValue].type
        );

        formData.append(
            "description",
            this.state.description
        );

        // Request made to the backend api
        // Send formData object
        axios.post(this.state.apiUrl + '/upload', formData);
    };

    fileData = () => {
        //console.log(this.state.selectedFile);
        if (this.state.selectedFile) {
            return (
                <p className='m-t-10px'>File Name: {this.state.selectedFile.name}</p>
            );
        } 
    };

    getFiles = () => {

    }

    render() {
        return (
            <>
                <div className='App'>
                    <header className='App-header'>
                        <img src={logo} className='App-logo' alt='logo' />
                        <h3>File Upload using React!</h3>
                    </header>
                    <div id='UploadTable'>
                        <table className='table table-hover table-bordered'>
                            <tbody>
                                <tr>
                                    <td className='w-15-pct'>Description</td>
                                    <td><input type='text' id='description' className='form-control' onChange={this.onDescChange} required /></td>
                                </tr>
                                <tr>
                                    <td className='w-15-pct'>Type</td>
                                    <td>
                                        <select className='Left m-l-10px' onChange={this.onTypeChange} value={this.state.selectValue} required>
                                            {this.state.selectOptions}
                                        </select>
                                    </td>
                                </tr>
                                <tr>
                                    <td className='w-15-pct'>Image</td>
                                    <td>
                                        <input type='file' className='form-control' onChange={this.onFileChange} required />
                                        <small className='form-text text-muted'>{this.state.fileTypeHelp}</small>
                                    </td>
                                </tr>
                            </tbody>
                        </table>
                        <button type='button' className='btn btn-primary' onClick={this.onSubmit}>
                            <span></span> Upload File
                        </button>
                        {this.fileData()}
                    </div>
                </div>
            </>
        );
    }
}

export default App;
